import React from "react";
import styled, {css} from 'styled-components';

const Circle = styled.div`
  width: 5rem;
  height: 5rem;
  background: ${props => props.color};
  border-radius: 50%;
  ${props => props.huge &&
          css`
            width: 10rem;
            height: 10rem;
          `}
`;

const AppBlock = styled.div`
  width: 512px;
  margin: 0 auto;
  margin-top: 4rem;
  border: 1px solid black;
  padding: 1rem;
`

function App() {
    return (
        <>
            <Circle color="black"/>
            <Circle color="blue" huge/>
        </>
    );
}

export default App;
