//     css`
//       width: 100%;
//       justify-content: center;
//       & + & {
//         margin-left: 0;
//         margin-top: 1rem;
//       }
//     `}
// `;

Button.defaultProps = {
    color: 'blue',
    size: 'medium'
};

export default Button;
